function tridiagonal_solver()
    % TRIDIAGONAL MATRIX SOLVER - Thomas Algorithm
    % This program solves systems of linear equations Ax = d
    % where A is a tridiagonal matrix

    clc;
    clear;

    % Display welcome message and instructions
    display_welcome();

    % Main menu
    while true
        choice = menu('Tridiagonal Matrix Solver', ...
                     'Learn about the Thomas Algorithm', ...
                     'Solve with Example Matrix', ...
                     'Input Your Own Matrix', ...
                     'Exit');

        switch choice
            case 1
                explain_algorithm();
            case 2
                solve_example();
            case 3
                solve_custom();
            case 4
                disp('Thank you for using the Tridiagonal Matrix Solver!');
                break;
        end
    end
end

function display_welcome()
    fprintf('\n========================================\n');
    fprintf('  TRIDIAGONAL MATRIX SOLVER\n');
    fprintf('  Thomas Algorithm Implementation\n');
    fprintf('========================================\n\n');
    fprintf('This program solves systems of equations Ax = d\n');
    fprintf('where A is a tridiagonal matrix.\n\n');
end

function explain_algorithm()
    clc;
    fprintf('\n========================================\n');
    fprintf('  THOMAS ALGORITHM EXPLANATION\n');
    fprintf('========================================\n\n');

    fprintf('The Thomas Algorithm solves tridiagonal systems efficiently.\n\n');

    fprintf('TRIDIAGONAL MATRIX FORM:\n');
    fprintf('A tridiagonal matrix has non-zero elements only on:\n');
    fprintf('  - Main diagonal (b)\n');
    fprintf('  - Lower diagonal (a)\n');
    fprintf('  - Upper diagonal (c)\n\n');

    fprintf('Matrix structure:\n');
    fprintf('  [b1  c1   0   0  ...  0 ]\n');
    fprintf('  [a2  b2  c2   0  ...  0 ]\n');
    fprintf('  [ 0  a3  b3  c3  ...  0 ]\n');
    fprintf('  [ :   :   :   :  ...  : ]\n');
    fprintf('  [ 0   0   0   0  ... bn]\n\n');

    fprintf('ALGORITHM STEPS:\n\n');
    fprintf('Step 1: FORWARD ELIMINATION\n');
    fprintf('  For i = 2 to n:\n');
    fprintf('    m = a(i) / b(i-1)\n');
    fprintf('    b(i) = b(i) - m * c(i-1)\n');
    fprintf('    d(i) = d(i) - m * d(i-1)\n\n');

    fprintf('Step 2: BACK SUBSTITUTION\n');
    fprintf('  x(n) = d(n) / b(n)\n');
    fprintf('  For i = n-1 to 1:\n');
    fprintf('    x(i) = (d(i) - c(i) * x(i+1)) / b(i)\n\n');

    fprintf('Time Complexity: O(n)\n');
    fprintf('Space Complexity: O(n)\n\n');

    input('Press Enter to continue...');
end

function solve_example()
    clc;
    fprintf('\n========================================\n');
    fprintf('  EXAMPLE PROBLEM\n');
    fprintf('========================================\n\n');

    % Example system
    a = [0, 1, 1, 1];      % Lower diagonal (first element unused)
    b = [2, 2, 2, 2];      % Main diagonal
    c = [1, 1, 1, 0];      % Upper diagonal (last element unused)
    d = [5, 6, 6, 5];      % Right-hand side

    fprintf('Solving the system:\n');
    fprintf('  2x1 + 1x2           = 5\n');
    fprintf('  1x1 + 2x2 + 1x3     = 6\n');
    fprintf('        1x2 + 2x3 + 1x4 = 6\n');
    fprintf('              1x3 + 2x4 = 5\n\n');

    % Display matrix form
    display_tridiagonal_matrix(a, b, c, d);

    % Solve with detailed steps
    x = thomas_algorithm_detailed(a, b, c, d);

    % Display solution
    fprintf('\n========================================\n');
    fprintf('  SOLUTION\n');
    fprintf('========================================\n\n');
    for i = 1:length(x)
        fprintf('  x%d = %.6f\n', i, x(i));
    end
    fprintf('\n');

    % Verify solution
    verify_solution(a, b, c, d, x);

    input('Press Enter to continue...');
