function solve_custom()
    clc;
    fprintf('\n========================================\n');
    fprintf('  CUSTOM MATRIX INPUT\n');
    fprintf('========================================\n\n');

    % Get matrix size
    n = input('Enter the size of the matrix (n x n): ');

    if n < 2
        fprintf('Error: Matrix size must be at least 2.\n');
        input('Press Enter to continue...');
        return;
    end

    fprintf('\nEnter the diagonals:\n');
    fprintf('(Note: First element of lower diagonal and last element of upper diagonal are not used)\n\n');

    % Input lower diagonal
    a = zeros(1, n);
    fprintf('Lower diagonal (enter %d values, first will be ignored):\n', n);
    for i = 2:n
        a(i) = input(sprintf('  a(%d) = ', i));
    end

    % Input main diagonal
    b = zeros(1, n);
    fprintf('\nMain diagonal (enter %d values):\n', n);
    for i = 1:n
        b(i) = input(sprintf('  b(%d) = ', i));
    end

    % Input upper diagonal
    c = zeros(1, n);
    fprintf('\nUpper diagonal (enter %d values, last will be ignored):\n', n);
    for i = 1:n-1
        c(i) = input(sprintf('  c(%d) = ', i));
    end

    % Input right-hand side
    d = zeros(1, n);
    fprintf('\nRight-hand side vector (enter %d values):\n', n);
    for i = 1:n
        d(i) = input(sprintf('  d(%d) = ', i));
    end

    % Display the matrix
    fprintf('\n');
    display_tridiagonal_matrix(a, b, c, d);

    % Ask if user wants detailed steps
    show_details = input('\nShow detailed solution steps? (1=Yes, 0=No): ');

    if show_details
        x = thomas_algorithm_detailed(a, b, c, d);
    else
        x = thomas_algorithm(a, b, c, d);
    end

    % Display solution
    fprintf('\n========================================\n');
    fprintf('  SOLUTION\n');
    fprintf('========================================\n\n');
    for i = 1:length(x)
        fprintf('  x%d = %.6f\n', i, x(i));
    end
    fprintf('\n');

    % Verify solution
    verify_solution(a, b, c, d, x);

    input('Press Enter to continue...');
end

